        {
            console.log(onChildLoss)
            this.onChildLoss();
            onExpense();
        }
        return (
            <>

             <div>

            <h1>{name}:{this.state.balance}


            </h1>
            {/* <button onClick ={onProfit}>Profit</button> */}
