import React, {Component} from "react";
import ChildComponet from "./ChildComponent";

class ParentComponent extends Component
{
    state =
    {
         balance :0,
    };
    onProfit =() =>
    {
        this.setState({balance : this.state.balance +1})
    }
    onExpense =() =>
    { 
        if(this.state.balance===0)
        {
            return;
        }
        this.setState({ balance: this.state.balance -1})
    }

    render()
    {
        return(
            <div>
            <h1>Parent : {this.state.balance}
             <button onClick={this.onProfit} >
                 family Profit</button>
            <button onClick={this.onExpense} >
                family Loss</button>
            </h1> 
            <ChildComponet name={"Ram"} onProfit={this.onProfit} onExpense={this.onExpense}/>
            <ChildComponet name={"krushna"} onProfit={this.onProfit} onExpense={this.onExpense}/>



           </div>

        );
    }
}

export default ParentComponent;
