import React, {Component} from "react";

class ChildComponet extends Component
{
    state =
    {
         childreanbalance :0,
    };
    onbalance =() =>
    {
        this.setState({childreanbalance : this.state.childreanbalance +1})
    }
    render()

    {
        const {name,onProfit,onExpense}=this.props;
        return (
            <>

             <div>

            <h1>{name} {this.state.childreanbalance}


            </h1>
            <button onClick ={onProfit,this.onbalance}>Profit</button>

            <button onClick ={onExpense}>Loss</button>


             </div>

         </>
        );

    }
}
export default ChildComponet;
